import React from 'react'
import { useState } from 'react/cjs/react.development'

const StateProduct  = () =>{

    const initial = {
        cart:[],
        carrito:[],
         product:[
            {
                'id':'1',
                'image':'data:image/jpeg;base64,',
                'title':'camiseta',
                'price' :25,
                'descripction':'lore '
            },
            {
                'id':'2',
                'image':'data:image/jpeg;base64,',
                'title':'pantalon',
                'price' :25,
                'descripction':'lore re'
            },
            {
                'id':'3',
                'image':'data:image/jpeg;base64,',
                'title':'sueter',
                'price' :58,
                'descripction':'lore er'
            },
         ]
    }
    
    
    const [state,setState] =useState(initial)

    const [nombre,setNombre]  =useState()

    const inputNmae =(e) => {
        setNombre(e.target.value)
    }
    const addCart =() => {
        const item = nombre
        setState({
            ...state,
            cart:[...state.cart,item]
        })
    }

    const remove  =(ingresar)=>{
        setState({
            ...state,
            carrito:state.carrito.filter(item =>  item.id != ingresar.id )
        })
    }

    const addProduct = (ingresar) => {
        setState({
            ...state,
            carrito:[...state.carrito,ingresar]
        })
    }

    return{addCart,
        inputNmae,
        nombre,
        addProduct,
        remove,
        state
    }
}
export default StateProduct